import React , {useState}from 'react';
import './signup.css';

function Signup() {
  const [Gmail, setGmail] = useState('')
  const handleGmail = (e) => {
    setGmail(e.target.value)
    console.log(e.target.value);
    
  }
  const handleSubmit = (event) => {
    event.preventDefault();
    sendEmail();
  };
  const sendEmail = async () => {
    try {
      const response = await fetch('http://localhost:5000/Send', {
        method: 'POST',
      });
      if (response.ok) {
        console.log('Email sent successfully');
      } else {
        console.error('Failed to send email');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (

    <form class="form_main" onSubmit={handleSubmit}>
      <p class="heading">SignUp</p>
      <div class="inputContainer">
        <input required placeholder="First Name" id="username" class="inputField" type="text" />
      </div>

      <div class="inputContainer">
        <input reqplaceholder="Second Name" id="username" class="inputField" type="text" />
      </div>

      <div class="inputContainer">
        <input placeholder="Gmail" id="username" class="inputField" type="email" onClick={handleGmail} value={Gmail}/>
      </div>

      <div class="inputContainer">
        <input placeholder="Password" id="password" class="inputField" type="password" />
      </div>

      <button id="button" onClick={sendEmail}>Submit</button>
      <a href="/OTP">Verify OTP</a>
      <div class="signupContainer">
        <p>Already Have an account?</p>
        <a href="/Login">Login</a>
      </div>
    </form>
  );
}

export default Signup;
