import React, { useState } from 'react';
import axios from 'axios'
import './Login.css';

function Login() {
    const [firstName, setFirstName] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [hasSubmitted, setHasSubmitted] = useState(false);

    const handleFirstNameChange = (e) => {
        setFirstName(e.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        setIsSubmitting(true);
        try {
            const response = await axios.post('http://localhost:5000/Send', { firstName });
            if (response.status === 200) {
                console.log('Gmail Registerd');
                setHasSubmitted(true);
            } else {
                console.error(`Gmail Not Registered: ${response.status}`);
            }
        } catch (error) {
            console.error('Error:', error.message);
        } finally {
            setIsSubmitting(false);
            event.preventDefault();
        }
    };
    return (
        <form class="form_main" action="">
            <p class="heading">Login</p>
            <div class="inputContainer">
                <input placeholder="Username" id="username" class="inputField" type="text" value={firstName} onChange={handleFirstNameChange} />
            </div>

            <div class="inputContainer">
                <input placeholder="Password" id="password" class="inputField" type="password" />
            </div>


            <br /><a href="">Forget Password</a><br />
            <button id="button" disabled={isSubmitting}>
            {isSubmitting ? 'Log...' : 'Login'}
            </button>            <div class="signupContainer">
                <p>Don't have any account?</p>
                <a href="/Signup">Sign up</a>
            </div>
        </form>
    );
}

export default Login;
