import axios from 'axios'
import './Login.css';

function Login() {
    const handleSubmit = async (event) => {
        event.preventDefault();
        setIsSubmitting(true);
        try {
          const response = await axios.post('http://localhost:5000/Send', { gmail });
          if (response.status === 200) {
            console.log('Gmail');
            setHasSubmitted(true);
          } else {
            console.error(`Failed to send email: ${response.status}`);
          }
        } catch (error) {
          console.error('Error:', error.message);
        } finally {
          setIsSubmitting(false);
          event.preventDefault();
        }
      };
    return (
        <form class="form_main" action="">
            <p class="heading">Login</p>
            <div class="inputContainer">
                <input placeholder="Username" id="username" class="inputField" type="text" />
            </div>

            <div class="inputContainer">
                <input placeholder="Password" id="password" class="inputField" type="password" />
            </div>


            <br /><a href="">Forget Password</a><br />
            <button id="button">Submit</button>
            <div class="signupContainer">
                <p>Don't have any account?</p>
                <a href="/Signup">Sign up</a>
            </div>
        </form>
    );
}

export default Login;
